<?phpdefined( '_JEXEC' ) or die( 'Restricted access' );class gustVMtoolsControllerGotadresses extends gustVMtoolsController{	function __construct(){		parent::__construct();	}	function apply(){		$gotadresses = $this->getModel("gotadresses");		$gotadresses->got();		$this->setRedirect('index.php?option=com_gustvmtools&view=gotadresses&task=gotadresses');	}	function cancel(){		$this->setRedirect('index.php?option=com_gustvmtools');	}}