<?php// Запрет прямого доступа.defined('_JEXEC') or die; // Подключаем библиотеку modellist Joomla.jimport('joomla.application.component.modellist'); /** * Модель списка сообщений компонента HelloWorld. */class gustVMtoolsModelNetproducts extends JModelList{    /**     * Метод для построения SQL запроса для загрузки списка данных.     *     * @return string SQL запрос.     */	 	public function __construct($config = array())	 {	 if (empty($config['filter_fields'])) {	 $config['filter_fields'] = array(	 'id', 'id_net', 'price', 'id_product', 'published'	 );	 }	 	 parent::__construct($config);	 }	protected function getListQuery()    {        // Создаем новый query объект.		$orderCol	= $this->state->get('list.ordering');		$orderDirn	= $this->state->get('list.direction');		$search = $this->getState('filter.search');		$search2 = $this->getState('filter.search2');		$search3 = $this->getState('filter.search3');        $db = JFactory::getDBO();        $query = $db->getQuery(true);        $query->select('*');        $query->from('#__vmtools_nets_products');		$query->order($db->getEscaped($orderCol.' '.$orderDirn));		if (($search) && (is_numeric($search)))		{		$query->where('id_net = '.$search);		}		if (($search2) && (is_numeric($search2)))		{		$query->where('id = '.$search2);		}		if (($search3) && (is_numeric($search3)))		{		$query->where('id_product = '.$search3);		}        return $query;    }	public function unpublish($arr)	{				$db = JFactory::getDBO();		foreach ($arr as $key=>$val)		{			$query = 'UPDATE #__vmtools_nets_products SET published = 0 WHERE id='.$val;			$db->setQuery($query);			$db->query();		}			}	public function delete_netproduct($arr)	{				$db = JFactory::getDBO();		foreach ($arr as $key=>$val)		{			$query = 'DELETE FROM #__vmtools_nets_products WHERE id ='.$val;			$db->setQuery($query);			$db->query();		}	}	public function publish($arr)	{				$db = JFactory::getDBO();		foreach ($arr as $key=>$val)		{			$query = 'UPDATE #__vmtools_nets_products SET published = 1 WHERE id='.$val;			$db->setQuery($query);			$db->query();		}			}	protected function populateState($ordering = null, $direction = null)	{		$search = $this->getUserStateFromRequest($this->context.'.filter.search', 'filter_search');		$this->setState('filter.search', $search);				$search2 = $this->getUserStateFromRequest($this->context.'.filter.search2', 'filter_search2');		$this->setState('filter.search2', $search2);				$search3 = $this->getUserStateFromRequest($this->context.'.filter.search3', 'filter_search3');		$this->setState('filter.search3', $search3);				parent::populateState('id', 'asc');	}}