<?php// Запрет прямого доступа.defined('_JEXEC') or die; // Подключаем библиотеку modellist Joomla.jimport('joomla.application.component.modellist'); /** * Модель списка сообщений компонента HelloWorld. */class gustVMtoolsModelOpisaniya extends JModelList{    /**     * Метод для построения SQL запроса для загрузки списка данных.     *     * @return string SQL запрос.     */	 	public function __construct($config = array())	 {	 if (empty($config['filter_fields'])) {	 $config['filter_fields'] = array(	 'id', 'product_id', 'review', 'published'	 );	 }	 	 parent::__construct($config);	 }	protected function getListQuery()    {        // Создаем новый query объект.		$orderCol	= $this->state->get('list.ordering');		$orderDirn	= $this->state->get('list.direction');        $db = JFactory::getDBO();        $query = $db->getQuery(true);        $query->select('*');        $query->from('#__vmtools_opisanie');		$query->order($db->getEscaped($orderCol.' '.$orderDirn));		        return $query;    }	public function unpublish($arr)	{				$db = JFactory::getDBO();		foreach ($arr as $key=>$val)		{			$query = 'UPDATE #__vmtools_opisanie SET published = 0 WHERE id='.$val;			$db->setQuery($query);			$db->query();		}			}	public function delete_otziv($arr)	{				$db = JFactory::getDBO();		foreach ($arr as $key=>$val)		{			$query = 'DELETE FROM #__vmtools_opisanie WHERE id ='.$val;			$db->setQuery($query);			$db->query();		}	}	public function publish($arr)	{				$db = JFactory::getDBO();		foreach ($arr as $key=>$val)		{			$query = 'UPDATE #__vmtools_opisanie SET published = 1 WHERE id='.$val;			$db->setQuery($query);			$db->query();		}			}	protected function populateState($ordering = null, $direction = null)	{		parent::populateState('id', 'asc');	}}