<?php// Запрет прямого доступа.defined('_JEXEC') or die; // Подключаем библиотеку modellist Joomla.jimport('joomla.application.component.modeladmin'); /** * Модель списка сообщений компонента HelloWorld. */class gustVMtoolsModelOtziv extends JModelAdmin{    /**     * Метод для построения SQL запроса для загрузки списка данных.     *     * @return string SQL запрос.     */	public function getForm($data = array(), $loadData = true) 	{		 // Get the form.		 $form = $this->loadForm('com_gustvmtools.otziv', 'otziv', array('load_data' => $loadData));		 if (empty($form)) 		 {		 return false;		 }		 return $form;	}	protected function loadFormData()	{		$id = JRequest::getVar('id');		$table = JModel::getTable('otziv');		$table->load($id);		$data = $table;		return $data;	}	    public function set_otziv_change()	{		$row =& JTable::getInstance('otziv', 'Table');		$form = JRequest::get('post');		$row->bind($form);		$row->check($form);		$row->store($form, true);	}	 public function add_otziv()	{		$row =& JTable::getInstance('otziv', 'Table');		$form = JRequest::get('post');		$row->bind($form);		$row->check($form);		$row->store($form, true);	}}