<?php// Запрет прямого доступа.defined('_JEXEC') or die; // Подключаем библиотеку modellist Joomla.jimport('joomla.application.component.modellist'); /** * Модель списка сообщений компонента HelloWorld. */class gustVMtoolsModelRegions extends JModelList{    /**     * Метод для построения SQL запроса для загрузки списка данных.     *     * @return string SQL запрос.     */	 	public function __construct($config = array())	 {	 if (empty($config['filter_fields'])) {	 $config['filter_fields'] = array(	 'label', 'id_region'	 );	 }	 	 parent::__construct($config);	 }	protected function getListQuery()    {        // Создаем новый query объект.		$orderCol	= $this->state->get('list.ordering');		$orderDirn	= $this->state->get('list.direction');        $db = JFactory::getDBO();        $query = $db->getQuery(true);        $query->select('*');        $query->from('#__vmtools_regions AS a');		$query->order($db->getEscaped($orderCol.' '.$orderDirn));		        return $query;    }	protected function populateState($ordering = null, $direction = null)	{		parent::populateState('label', 'asc');	}}