<?php// Запрет прямого доступа.defined('_JEXEC') or die; // Подключаем библиотеку modellist Joomla.jimport('joomla.application.component.modellist'); /** * Модель списка сообщений компонента HelloWorld. */class gustVMtoolsModelObzor extends JModelList{    /**     * Метод для построения SQL запроса для загрузки списка данных.     *     * @return string SQL запрос.     */	 	public function __construct($config = array())	 {		parent::__construct($config);	 }	protected function getListQuery()    {        // Создаем новый query объект.		        $db = JFactory::getDBO();        $query = $db->getQuery(true);        $query->select('vo.review AS text, ns.product_name, vo.product_id');        $query->from('#__vmtools_opisanie AS vo, #__virtuemart_products_ru_ru AS ns');		$query->where('vo.product_id = ns.virtuemart_product_id');		$query->order("vo.id DESC");		        return $query;    }	protected function populateState($ordering = null, $direction = null)	{		parent::populateState('id', 'asc');	}}